mapper.insert:
insert into notice (
notice_no,notice_title,notice_content,notice_date,manager_no
)
values(
seq_notice.nextval,
#{notice_title},
#{notice_content},
SYSDATE,
#{manager_no}
)

mapper.update:
update notice set
notice_title=#{notice_title},
notice_content=#{notice_content}
where
notice_no=#{notice_no}
